import React from 'react'
import AppHeader from '../app-header/app-hearder'
import SearchPanel from '../search-panel/search-panel'
import PostStatusFilter from '../post-status-filter/post-status-filter'
import PostList from '../post-list/post-list'
import PostAddForm from '../post-add-form/post-add-form' 

//import css files
import './app.css'
import '../post-add-form/post-add-form.css'
import '../post-list/post-list.css'
import '../post-list-item/post-list-item.css'
import '../post-status-filter/post-status-filter.css'
import '../search-panel/search-panel.css'
import '../app-header/app-header.css'
import styled from 'styled-components'

const AppBlock = styled.div`
        margin: 0 auto;
        max-width: 800px;
`

export default class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data : [
                {label:"Going to market", important:false, id:'alkd'},
                {label:"Learn Js", important:true, id:'aiekd'},
                {label:"Lear React tutorial", important:false, id:'alkdiiek'}
            ]
        }
    }
    deleteItem = (id) => {
        console.log(id);
    }

    render() {
        return (
            <AppBlock>
                <AppHeader></AppHeader>
                <div className="search-panel d-flex">
                    <SearchPanel></SearchPanel>
                    <PostStatusFilter></PostStatusFilter>
                </div>
                <PostList 
                    props={this.state.data}
                    onDelete={(id) => this.deleteItem(id)}
                >

                </PostList>
                <PostAddForm></PostAddForm>
            </AppBlock>
        )
    }
}
